INSERT INTO `system_text` (`id`, `lang`, `text`, `author`, `author_edit`, `time_create`, `time_edit`) VALUES ('tipps', 'deDE', '<h3>Got Prolems using slingit.org? Here\'s a checklist you might want to have a look at ;)</h3><ol><li>Upload funktioniert nicht check if you use firefox or chrome. Otherwise slingit.org might not work properly, since WebRTC is not standart to all Browsers.<br><br></li><li>reload the page multiple times and try again.<br><br></li><li>check file size. use 7-Zip or winrar to minimize the files you intend to share.<br><br></li><li>warning:files located inside archives like .zip format are no valid data. you can only share the full .zip.<br><br></li><li>in order to upload the file to it\'s destination, it is necessary to hold your tab open.<br><br></li><li>The upload speed will be displayed in your log.<br><br></li><li>check if your partner uses firefox or chrome.<br><br></li><li>Large files may take long timespans to index.<br><br></li><li>Possible filesize is determined by your RAM. There is an individual Limit to filesize.</li></ol>', 0, 1, '2015-04-17 00:00:45', '2015-04-17 00:00:45');
INSERT INTO `system_text` (`id`, `lang`, `text`, `author`, `author_edit`, `time_create`, `time_edit`) VALUES ('tipps', 'enUS', '<h3>Got Prolems using slingit.org? Here\'s a checklist you might want to have a look at ;)</h3><ol><li>Upload funktioniert nicht check if you use firefox or chrome. Otherwise slingit.org might not work properly, since WebRTC is not standart to all Browsers.<br><br></li><li>reload the page multiple times and try again.<br><br></li><li>check file size. use 7-Zip or winrar to minimize the files you intend to share.<br><br></li><li>warning:files located inside archives like .zip format are no valid data. you can only share the full .zip.<br><br></li><li>in order to upload the file to it\'s destination, it is necessary to hold your tab open.<br><br></li><li>The upload speed will be displayed in your log.<br><br></li><li>check if your partner uses firefox or chrome.<br><br></li><li>Large files may take long timespans to index.<br><br></li><li>Possible filesize is determined by your RAM. There is an individual Limit to filesize.</li></ol>', 0, 1, '2015-04-17 00:01:21', '2015-04-17 00:01:21');
